function getWeb3(){
	return TEXT_MODE_ACTUAL === TEXT_MODE_TEST ? WEB3_DATA_TEST : WEB3_DATA;
}
export function getBabytoshiAddress(){
	return TEXT_MODE_ACTUAL === TEXT_MODE_TEST ? BABYTOSHI_ADDRESS_TEST : BABYTOSHI_ADDRESS;
}

export function getContractLinkBabytoshi(){
	return TEXT_MODE_ACTUAL === TEXT_MODE_TEST ? CONTRACT_LINK_TEST : CONTRACT_LINK;
}

export function getPancakePairAddress(){
	return TEXT_MODE_ACTUAL === TEXT_MODE_TEST ? BABYTOSHI_PANCAKE_PAIR_TEST : BABYTOSHI_PANCAKE_PAIR;
}

export async function getTotalSupply(){
	let web3Data = getWeb3();
	let playersAddressData = getBabytoshiAddress();
    let contract = new web3Data.eth.Contract(BABYTOSHI_ABI, playersAddressData);
    if( contract ){
        let amount = await contract.methods.totalSupply().call().then( (_amount) => {
			return _amount;
            //return _amount;
        });
        return amount;
    }
    return 0;
}

export async function getLiquidityPoolBabytoshi(){
	let web3Data = getWeb3();
	let playersAddressData = getPancakePairAddress();
    let contract = new web3Data.eth.Contract(PANCAKE_ABI, playersAddressData);
    if( contract ){

		let liquidityPool = await contract.methods.getReserves().call().then( async (reserves)=>{
			let _liquidityPool = 0;
			let token0 = await contract.methods.token0().call().then((token)=>{return token;}).catch(()=>{return '';});
			if( token0.toLowerCase() === BABYTOSHI_ADDRESS.toLowerCase() || token0.toLowerCase() === BABYTOSHI_ADDRESS_TEST.toLowerCase() ){
				_liquidityPool = reserves._reserve0;
			}else{
				_liquidityPool = reserves._reserve1;
			}
			return _liquidityPool;
		}).catch((error)=>{
			return 0;
		});
		return liquidityPool;
    }
    return 0;
}

