export async function claimRewards(ethereum, player=new Player()){
	let babytoshiAddressDAta = getBabytoshiAddress();
    let contract = new (new Web3(ethereum)).eth.Contract(BABYTOSHI_ABI, babytoshiAddressDAta);
    if( player.key !== TEXT_ADDRESS_ZERO ){
        if( contract ){
            await contract.methods.claim().send({from: player.key}).then( async () => {
                //await updatePlayer();
                //await init();
				//return web3Data.utils.fromWei(data.toString(), 'ether');
                return true;
            }); 
        }
    }
    return false;
}
